import React, { useState } from 'react'
import ScheduleBooking from '../components/ScheduleBooking'
import Form from '../components/Form';
import Successful from '../components/Successful';
import MultiForm from '../components/MutliForm';


export const AppContext = React.createContext({})
const Booking = () => {

    const components = [<ScheduleBooking />, <Form />, <Successful />, <MultiForm />];
    const [currentComponentIndex, setCurrentComponentIndex] = useState(0);
    const handleButtonClick = () => {
        setCurrentComponentIndex((prevIndex) => (prevIndex + 1) % components.length);
        console.log('next', currentComponentIndex);
    };
    const prevHandelClick = () => {
        setCurrentComponentIndex((prevIndex) => (prevIndex - 1 + components.length) % components.length);
        console.log('prev', currentComponentIndex);
    }
    return (
        <AppContext.Provider value={{}}>
            <form action="" >
                <div className='card  mx-auto mt-5 justify-content-center align-items-center' style={{ width: "80vw", height: '80vh' }}>
                    <div>
                        {components[currentComponentIndex]}
                    </div>
                </div>
                <div className='d-flex'>

                    {currentComponentIndex !== 0 && < div className='d-flex  mx-auto border-secondary m-2 justify-content-between p-2 align-items-center gap-2'>
                        <button type='button' onClick={prevHandelClick} style={{ width: '10vw' }} className='btn btn-primary '>Prev</button>
                    </div>}

                    {currentComponentIndex === components.length - 1 ?
                        <div className='d-flex  mx-auto border-secondary m-2 justify-content-between p-2 align-items-center gap-2'>
                            <button type='button' style={{ width: '10vw' }} className='btn btn-primary '>Submit</button>
                        </div> :
                        <div className='d-flex  mx-auto border-secondary m-2 justify-content-between p-2 align-items-center gap-2'>
                            <button type='button' onClick={handleButtonClick} style={{ width: '10vw' }} className='btn btn-primary '>Next....</button>
                        </div>}
                </div>
            </form>
        </AppContext.Provider >
    )
}

export default Booking
