import React, { useState, useEffect } from 'react'

const ScheduleBooking = () => {
    const [monthData, setMonthData] = useState([]);
    const [displayedData, setDisplayedData] = useState([]);
    const [startIndex, setStartIndex] = useState(0);
    const button = [
        '10:00 AM',
        '10:30 AM',
        '11:00 AM',
        '11:30 AM',
        '12:00 PM',
        '12:30 PM',
        '01:00 PM',
        '01:30 PM',
        '02:00 PM',
        '02:30 PM',
        '03:00 PM',
        '03:30 PM',
        '04:00 PM',

    ]

    useEffect(() => {
        // Get the current date
        const currentDate = new Date();

        // Calculate the date of 1 month later
        const oneMonthLaterDate = new Date(currentDate);

        oneMonthLaterDate.setMonth(oneMonthLaterDate.getMonth() + 1);

        const data = [];
        let currentDateIterator = new Date(currentDate);

        while (currentDateIterator <= oneMonthLaterDate) {
            data.push({
                date: new Date(currentDateIterator),
            });
            currentDateIterator.setDate(currentDateIterator.getDate() + 1);
        }
        setMonthData(data);
        setDisplayedData(data.slice(startIndex, startIndex + 3));
    }, [startIndex]);


    const handleNextClick = () => {
        if (startIndex + 3 < monthData.length) {
            setStartIndex(startIndex + 3);


        }
    };
    const handlePreviewClick = () => {
        if (startIndex - 3 >= 0) {
            setStartIndex(startIndex - 3);
        }
    };
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedTime, setSelectedTIme] = useState(null);
    const [isButtonClicked, setButtonClicked] = useState('');

    const handleDateClick = (selectedDate, index) => {
        setSelectedDate(selectedDate);

    };
    const handleTimeClick = (selectedTime) => {
        setSelectedTIme(selectedTime);


        setButtonClicked(index)
        console.log(isButtonClicked);
    };


    return (
        <>
            <div className='d-flex justify-content-center flex-column  mt-5 m-3'>
                <div className='d-flex mx-auto '>
                    <h1>Appointment Booking</h1>
                </div>
                <div className='d-flex border mx-auto border-secondary m-2 justify-content-between p-2 align-items-center gap-2 ' style={{ width: '70vw' }}>
                    <button style={{ maxWidth: '10vw', height: '5vh' }} className='btn btn-outline-dark' onClick={handlePreviewClick} disabled={startIndex === 0}> Preview </button>
                    {displayedData.map((item, index) => (
                        <button onClick={() => handleDateClick(item.date.toLocaleDateString(), index)} key={index} style={{ width: '15vw', border: '1px solid black', borderRadius: '4px', padding: '5px' }} >
                            {item.date.toLocaleDateString()}
                        </button>
                    ))}
                    <button style={{ width: '6vw', height: '5vh' }} className='btn btn-outline-dark' onClick={handleNextClick} disabled={startIndex + 3 >= monthData.length}>Next</button>
                </div>
            </div >

            <div className='d-flex justify-content-center flex-column align-items-centerm'>
                <div className='d-flex border mx-auto border-secondary  flex-wrap justify-content-evenly m-2 flex-wrap p-2 align-items-center gap-2' style={{ width: '70vw' }}>


                    {button.map((button, index) => (
                        <button
                            key={index}
                            onClick={() => handleTimeClick(index)}
                            style={{
                                backgroundColor: isButtonClicked === index ? 'green' : 'blue',
                                color: 'white',
                            }}
                        >
                            {button}
                        </button>
                    ))}

                </div>
            </div>
        </>
    )
}

export default ScheduleBooking
