import React, { useState, useEffect } from 'react';


const ScheduleBooking = (props) => {
    const [monthData, setMonthData] = useState([]);
    const [displayedData, setDisplayedData] = useState([]);
    const [startIndex, setStartIndex] = useState(0);

    const button = [
        '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM', '12:00 PM', '12:30 PM', '01:00 PM',
        '01:30 PM', '02:00 PM', '02:30 PM', '03:00 PM', '03:30 PM', '04:00 PM',
    ]

    useEffect(() => {
        const currentDate = new Date();
        const oneMonthLaterDate = new Date(currentDate);
        oneMonthLaterDate.setMonth(oneMonthLaterDate.getMonth() + 1);

        const data = [];
        let currentDateIterator = new Date(currentDate);

        while (currentDateIterator <= oneMonthLaterDate) {
            data.push({
                date: new Date(currentDateIterator),
            });
            currentDateIterator.setDate(currentDateIterator.getDate() + 1);
        }
        setMonthData(data);
        setDisplayedData(data.slice(startIndex, startIndex + 3));
    }, [startIndex]);

    const handleNextClick = () => {
        if (startIndex + 3 < monthData.length) {
            setStartIndex(startIndex + 3);
        }
    };

    const handlePreviewClick = () => {
        if (startIndex - 3 >= 0) {
            setStartIndex(startIndex - 3);
        }
    };


    const [TimeButtonClicked, setTimeButtonClicked] = useState(null);
    const [DateButtonClicked, setDateButtonClicked] = useState(null);


    const handleDateClick = (datePicker, index) => {
        setDateButtonClicked((prevIndex) => (prevIndex === index ? null : index));
        props.setData((prevData) => ({ ...prevData, selectedDate: datePicker }));



    };
    const handleTimeClick = (time, index) => {
        setTimeButtonClicked((prevIndex) => (prevIndex === index ? null : index));
        props.setData((prevData) => ({ ...prevData, selectedTime: time }));

    };

    useEffect(() => {
        console.log(DateButtonClicked);
        console.log(props.data);
        console.log(TimeButtonClicked);
        console.log(props.data);
    }, [DateButtonClicked, props.data, TimeButtonClicked, props.data]);

    return (
        <>

            <div className='d-flex justify-content-center flex-column  mt-5 m-3'>
                <div className='d-flex mx-auto '>
                    <h1>Appointment Booking</h1>
                </div>
                <div className='d-flex border mx-auto border-secondary m-2 justify-content-between p-2 align-items-center gap-2  rounded' style={{ width: '70vw' }}>
                    <button style={{ maxWidth: '10vw', height: '5vh' }} className='btn btn-primary' onClick={handlePreviewClick} disabled={startIndex === 0}> Preview </button>
                    {displayedData.map((datePicker, index) => (
                        <button onClick={() => handleDateClick(datePicker, index)} key={index}  style={{
                            background: DateButtonClicked === index ? 'green' : 'white',
                            color: DateButtonClicked === index ? 'white' : 'black',
                            border: 'none',
                            borderRadius: '4px',
                            padding: '4px'
                        }} >
                            {datePicker.date.toLocaleDateString()}
                        </button>
                    ))}
                    <button style={{ width: '6vw', height: '5vh' }} className='btn btn-primary' onClick={handleNextClick} disabled={startIndex + 3 >= monthData.length}>Next</button>
                </div>
            </div >

            <div className='d-flex justify-content-center flex-column align-items-centerm rounded'>
                <div className='d-flex border mx-auto border-secondary rounded flex-wrap justify-content-evenly m-2 flex-wrap p-4 align-items-center gap-2' style={{ width: '70vw' }}>
                    {button.map((time, index) => (
                        <button className=''
                            key={index}
                            onClick={() => handleTimeClick(time, index)}
                            style={{
                                background: TimeButtonClicked === index ? 'green' : 'white',
                                color: TimeButtonClicked === index ? 'white' : 'black',
                                border: '1px solid black',
                                borderRadius: '4px',
                                padding: '4px',
                                width: '15vw'
                            }}
                            active
                        >
                            {time}
                        </button>
                    ))}
                </div>
            </div>

        </>
    )
}

export default ScheduleBooking
