import React, { useState, useEffect } from 'react'

const ScheduleBooking = () => {
    const [data, setData] = useState([]);
    const [oneMonthLater, setOneMonthLater] = useState(null);

    useEffect(() => {
        // Get the current date
        const currentDate = new Date();

        // Add 1 month to the current date
        const oneMonthLaterDate = new Date(currentDate);
        oneMonthLaterDate.setMonth(oneMonthLaterDate.getMonth() + 1);

        // Update the state with the one month later date
        setOneMonthLater(oneMonthLaterDate);
    }, []); // Empty dependency array ensures that the effect runs only once on component mount

    return (
        <div className='d-flex justify-content-center flex-column mt-5'>
            <div className='d-flex  '>
                <h1>Appointment Booking</h1>
            </div>
            <div>
                <p>Current date: {new Date().toLocaleDateString()}</p>
                {oneMonthLater && (
                    <p>Date of 1 month later: {oneMonthLater.toLocaleDateString()}</p>
                )}
            </div>
        </div>
    )
}

export default ScheduleBooking
