import React, { useState, useEffect } from 'react'

const ScheduleBooking = () => {
    const [data, setData] = useState([]);
    const [monthData, setMonthData] = useState([]);

    useEffect(() => {
        // Get the current date
        const currentDate = new Date();

        // Calculate the date of 1 month later
        const oneMonthLaterDate = new Date(currentDate);
        oneMonthLaterDate.setMonth(oneMonthLaterDate.getMonth() + 1);

        // Create a dataset for each day in the next month
        const data = [];
        let currentDateIterator = new Date(currentDate);

        while (currentDateIterator <= oneMonthLaterDate) {
            data.push({
                date: new Date(currentDateIterator),
                // You can add additional properties or data as needed
            });

            currentDateIterator.setDate(currentDateIterator.getDate() + 1);
        }

        // Update the state with the dataset
        setMonthData(data);
    }, []); // Empty dependency array ensures that the effect runs only once on component mount

    return (
        <div className='d-flex justify-content-center flex-column mt-5'>
            <div className='d-flex  '>
                <h1>Appointment Booking</h1>
            </div>
            <div>
                <ul>
                    {monthData.map((item, index) => (
                        <li key={index}>
                            {item.date.toLocaleDateString()}
                            {/* You can access other properties of the item if needed */}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    )
}

export default ScheduleBooking
