import React, { useEffect, useState } from 'react';
import profile from '../assets/female_doctor.webp';

const Profile = (props) => {
  const userid = props.userID.userID;
  console.log('userid for backend from profile', userid);

  const [data, setData] = useState([]);
  const [editMode, setEditMode] = useState(false);
  const [editedData, setEditedData] = useState({});
  console.log(editedData);

  const url = `http://localhost:5050/auth/userdata?data=${encodeURIComponent(userid)}`;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }
        const data = await response.json();
        setData(data);
        setEditedData(data); // Initialize editedData with the fetched data
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [url]);

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = () => {
    // Perform save logic, update data on the server, etc.
    // For simplicity, let's just update the UI locally
    setData(editedData);
    setEditMode(false);
  };

  const handleCancelClick = () => {
    // Reset editedData to match the current data
    setEditedData(data);
    setEditMode(false);
  };

  const handleInputChange = (e) => {
    // Update the editedData state as the user types into the input fields
    setEditedData({
      ...editedData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className='d-flex justify-content-center align-items-center mx-auto  m-3 '>
      <div className='d-flex border border-primary justify-content-center align-items-center  flex-column ' style={{ width: '98%', height: '70vh' }}>
        <div >
          <img src={profile} alt="" style={{ width: '5rem', borderRadius: '50%' }} />
        </div>
        <div>
          <div className="d-flex flex-column" style={{ width: '20vw' }}>
            <label htmlFor="firstName">First Name</label>
            {editMode ? (
              <input
                type="text"
                id="firstName"
                className="p-1"
                name="firstName"
                value={editedData.firstName}
                onChange={handleInputChange}
              />
            ) : (
              <input
                type="text"
                id="firstName"
                className="p-1"
                value={data.firstName}
                readOnly
              />
            )}
          </div>
          <div className="d-flex flex-column " style={{ width: '20vw' }}>
            <label htmlFor="lastName">Last Name</label>
            {editMode ? (
              <input
                type="text"
                id="lastName"
                className="p-1"
                name="lastName"
                value={editedData.lastName}
                onChange={handleInputChange}
              />
            ) : (
              <input
                type="text"
                id="lastName"
                className="p-1"
                value={data.lastName}
                readOnly
              />
            )}
          </div>
          <div className="d-flex flex-column " style={{ width: '20vw' }}>
            <label htmlFor="email">Email</label>
            <input
              type="text"
              id="email"
              className="p-1"
              value={data.email}
              readOnly
            />
          </div>
          {editMode ? (
            <div>
              <button onClick={handleSaveClick}>Save</button>
              <button onClick={handleCancelClick}>Cancel</button>
            </div>
          ) : (
            <button onClick={handleEditClick}>Edit</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
