import React, { useEffect, useState } from "react";
import FirstPage from "../components/FirstPage";
import SecondPage from "../components/SecondPage";
import ThirdPage from "../components/ThirdPage";
import SuccessPage from "../components/Successful";
import axios from "axios";

export default function Form(props) {
    async function fetchSession() {
        try {
            const response = await axios.post('http://localhost:5050/auth/login', user, { withCredentials: true, headers: { 'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json' } });

            if (response.status === 200) {
                const data = response.data;
                localStorage.setItem('authToken', data.token);

            } else if (response.status === 401) {
                setSuccessMessage('user not found')
                console.log('user not found');
            } else {
                console.log('Login failed:', response.data.error);
            }
        } catch (error) {
            console.error('Error during login:', error.message);
        }
        const response = await axios.get("http://localhost:5050/auth/validatesession", { withCredentials: true, headers: { 'Access-Control-Allow-Origin': '*', 'Content-Type': 'application/json' } })
        console.log(response.status)
    }
    fetchSession()
    const [nextDates, setNextDates] = useState(5);
    const [appointmentData, setAppointmentData] = useState({
        userId: props.userID
    });
    console.log(appointmentData)
    const [currentPage, setCurrentPage] = useState("firstpage");
    const [loading, setLoading] = useState(false);


    return (
        <div
            className="d-flex flex-column  mx-auto  mt-5 justify-content-center align-items-center bg-light bg-opacity-25 p-3 w-75 border border-dark"
            style={{ height: "80vh", overflow: "hidden", borderRadius: "10px" }}
        >
            <h2 className="mt-2">Appointment Booking</h2>
            {currentPage === "firstpage" ? (
                <FirstPage
                    setCurrentPage={setCurrentPage}
                    setAppointmentData={setAppointmentData}
                    appointmentData={appointmentData}
                    nextDates={nextDates}
                    setNextDates={setNextDates}
                />
            ) : null}
            {currentPage === "secondpage" ? (
                <SecondPage
                    setCurrentPage={setCurrentPage}
                    setAppointmentData={setAppointmentData}
                    appointmentData={appointmentData}
                />
            ) : null}
            {currentPage === "thirdpage" ? (
                <ThirdPage
                    setCurrentPage={setCurrentPage}
                    setAppointmentData={setAppointmentData}
                    appointmentData={appointmentData}
                    setLoading={setLoading}
                />
            ) : null}
            {currentPage === "success" ? <SuccessPage loading={loading} /> : null}
        </div>
    );
}
