import React, { useState } from 'react';

const MultiForm = () => {
  const [formData, setFormData] = useState({
    step1: { selectedDate: null, selectedTime: null },
    step2: { additionalInfo: '' },
    // Add more steps as needed
  });

  const handleStep1 = (selectedDate, selectedTime) => {
    setFormData((prevData) => ({
      ...prevData,
      step1: { selectedDate, selectedTime },
    }));
  };

  const handleStep2 = (additionalInfo) => {
    setFormData((prevData) => ({
      ...prevData,
      step2: { additionalInfo },
    }));
  };

  // Add more functions for each step as needed

  const handleSubmit = () => {
    // Handle the submission of formData
    console.log('Form Data:', formData);
  };

  return (
    <div>
      {/* Step 1 */}
      <Step1 onSubmit={handleStep1} />

      {/* Step 2 */}
      <Step2 onSubmit={handleStep2} />

      {/* Add more steps as needed */}

      {/* Submit button */}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

const Step1 = ({ onSubmit }) => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);

  const handleNext = () => {
    // Validate data if needed

    // Pass data to parent component
    onSubmit(selectedDate, selectedTime);
  };

  return (
    <div>
      <h2>Step 1: Date and Time</h2>
      {/* Your date and time picker component goes here */}
      {/* ... */}

      <button onClick={handleNext}>Next</button>
    </div>
  );
};

const Step2 = ({ onSubmit }) => {
  const [additionalInfo, setAdditionalInfo] = useState('');

  const handleNext = () => {
    // Validate data if needed

    // Pass data to parent component
    onSubmit(additionalInfo);
  };

  return (
    <div>
      <h2>Step 2: Additional Information</h2>
      {/* Your additional information input component goes here */}
      {/* ... */}

      <button onClick={handleNext}>Next</button>
    </div>
  );
};

// Add more step components as needed

export default MultiForm;
