import React, { useState } from 'react'
import ScheduleBooking from '../components/ScheduleBooking'
import Form from '../components/Form';


const Booking = () => {
    const components = [<ScheduleBooking />, <Form />];
    const [currentComponentIndex, setCurrentComponentIndex] = useState(0);
    const [previewComponentIndex, setPreviewComponentIndex] = useState(null);
    const handleButtonClick = () => {
        // Toggle to the next component
        setCurrentComponentIndex((prevIndex) => (prevIndex + 1) % components.length);
        setPreviewComponentIndex(null)
    };
    const handlePreviewButtonClick = () => {
        // Set the preview to the next component without toggling
        setPreviewComponentIndex((prevIndex) => (prevIndex -1) % components.length);
    };
    return (
        <>
            <div>
                {components[currentComponentIndex]}
            </div>
            <div className='d-flex'>
                <div className='d-flex  mx-auto border-secondary m-2 justify-content-between p-2 align-items-center gap-2'>
                    <button onClick={handlePreviewButtonClick} style={{ width: '10vw' }} disabled={currentComponentIndex === 0} className='btn btn-primary '>Prev</button>
                </div>
                <div className='d-flex  mx-auto border-secondary m-2 justify-content-between p-2 align-items-center gap-2'>
                    <button onClick={handleButtonClick} style={{ width: '10vw' }} disabled={currentComponentIndex >= components.length} className='btn btn-primary '>Next....</button>
                </div>
            </div>
        </>
    )
}

export default Booking
