import React, { useState } from 'react';

const DateTimePicker = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [selectedButton, setSelectedButton] = useState(null);

  const timeSlots = ['9:00 AM', '10:00 AM', '11:00 AM', '12:00 PM', '1:00 PM', '2:00 PM', '3:00 PM', '4:00 PM', '5:00 PM', '6:00 PM'];
  const daysInMonth = Array.from({ length: 31 }, (_, index) => index + 1);

  const handleDateClick = (day) => {
    setSelectedDate(day);
    setSelectedButton(day);
  };

  const handleTimeClick = (time) => {
    setSelectedTime(time);
    setSelectedButton(time);
  };

  return (
    <div>
      <h2>Date and Time Picker</h2>

      <div>
        {daysInMonth.map((day) => (
          <button
            key={day}
            onClick={() => handleDateClick(day)}
            style={{ backgroundColor: selectedButton === day ? 'lightblue' : 'white' }}
          >
            {day}
          </button>
        ))}
      </div>

      <div>
        {timeSlots.map((time) => (
          <button
            key={time}
            onClick={() => handleTimeClick(time)}
            style={{ backgroundColor: selectedButton === time ? 'lightblue' : 'white' }}
          >
            {time}
          </button>
        ))}
      </div>

      <div>
        {selectedDate && selectedTime && (
          <p>
            Selected Date: {selectedDate} | Selected Time: {selectedTime}
          </p>
        )}
      </div>
    </div>
  );
};

export default DateTimePicker;
