import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './components/Navbar';
import { BrowserRouter, Route, Routes, useLocation } from 'react-router-dom';
import Home from './pages/Home';
import Treatment from './pages/Treatment';
import Blogs from './pages/Blogs';
import AboutMe from './pages/AboutMe';
import { useEffect, useState } from 'react';
import TreatmentDetails from './pages/TreatmentDetails';
import BlogDetails from './pages/BlogDetails';
import Booking from './pages/Booking';

function App() {
  const [treat, setTreat] = useState("");
  const [blog, setBlog] = useState("");
  const ScrollToTop = () => {
    const { pathname } = useLocation();

    useEffect(() => {
      // Scroll to the top of the page on every route change
      window.scrollTo(0, 0);
    }, [pathname]);

    return null; // This component doesn't render anything
  };
  return (
    <div className="App">
      <BrowserRouter>
        <Navbar />
        <ScrollToTop />
        <Routes>
          <Route path='/' element={<Home setTreat={setTreat} setBlog={setBlog} />} />
          <Route path="treatment" element={<Treatment setTreat={setTreat} />} />
          <Route path="blogs" element={<Blogs setBlog={setBlog} />} />
          <Route path="about-me" element={<AboutMe />} />
          <Route path="/treatment-details" element={<TreatmentDetails data={treat} />} />
          <Route path="/blogs-details" element={<BlogDetails data={blog} />} />
          <Route path='/booking' element={<Booking />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
